import React, { Component } from "react";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import {
  Container,
  Button,
  Box,
  Tooltip,
  FormGroup,
  FormControl,
  Checkbox,
  FormHelperText,
  FormControlLabel,
  FormLabel
} from "@material-ui/core";

// const healthServices = [
//     {key:'clinic',label:'Clinical Laboratory Services',value:'false'},
//     {key: 'physicalTherapy',label:'Physical Therapy Services',value:'false'},
//     {key:'radiology',label:'Radiology and Imaging Services',value:'false'}
// ]

export class Step4 extends Component {
  state = {
    // clinic: false,
    // physicalTherapy: false,
    // radiology: false,
    // radiation: false,
    // equipment: false,
    // nutrients: false,
    // prosthetics: false,
    // homeHealth: false,
    // outpatientDrugs: false,
    // hospitalServices: false,
    // other: '',
    healthServices: [
      { key: "clinic", label: "Clinical Laboratory Services", value: "false" },
      {
        key: "physicalTherapy",
        label: "Physical Therapy Services",
        value: "false"
      },
      {
        key: "radiology",
        label: "Radiology and Imaging Services",
        value: "false"
      }
    ]
  };

  handleCheck = name => event => {
    this.setState({ ...this.state, [name]: event.target.checked });
  };

  render() {
    return (
      <MuiThemeProvider>
        <React.Fragment>
          <Container maxwidth="sm">
            <h1>
              Does that healthcare provider furnish designated health services
              or DHS?
            </h1>
            <Box component="span">
              <Container maxWidth="sm">
                <FormControl component="fieldset">
                  <FormLabel component="legend">
                    Select one
                    <FormGroup>
                      {this.state.healthServices
                        .map((service, index) => (
                          <FormControlLabel
                            control={
                              <Checkbox
                                checked={service.value}
                                onChange={() => {
                                  this.setState(prevState => ({
                                    healthServices: prevState.healthServices.map(
                                      el =>
                                        el.key === service.key
                                          ? { ...el, value: !el.value }
                                          : el
                                    )
                                  }));
                                }}
                                label={service.label}
                                value={service.value}
                              />
                            }
                          />
                        ))
                        .reduce((prev, curr) => [prev, " ", curr])}
                    </FormGroup>
                  </FormLabel>
                </FormControl>
              </Container>
            </Box>
          </Container>
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

export default Step4;
