import React, { Component } from 'react';
import dialogueTree from './helpStructs.js';
import Step1 from './Step1';
import Ending from './Ending'
import AppBar from 'material-ui/AppBar';
import { MuiThemeProvider } from 'material-ui/styles';

export class UserForm extends Component {
    state = {
        step: 'Step1',
        prevStep: '',
        email: '',
        physician: false,
    }

    // Goto next step
    nextStep = (action) => {
        const { step } = this.state;
        this.setState({
            prevStep: step,
            step: dialogueTree[action]
        });
    }

    // Return to previously viewed step
    goBack = () => {
        const { step, prevStep } = this.state;
        this.setState({
            step: prevStep,
            prevStep: ''
        });
    }

    // Jump to specific step
    jumpToStep = (newStep) => {
        const {step, prevStep } = this.state;
        this.setState({
            prevStep: step,
            step: dialogueTree[newStep]
        })
    }

    // Goto 'no-risk' ending
    jumpToEnd = () => {
        const { step } = this.state;
        this.setState({
            step: 'End'
        })
    }

    // Auto save for textfield input
    handleChange = input => e => {
        this.setState({[input]: e.target.value});
    }

    render() {
        const { step, prevStep } = this.state;
        const { email, physician } = this.state;
        const values = { email, physician }
        
        switch(step) {
            case 'Step1':
                return (
                    <MuiThemeProvider>
                        <AppBar title="Stark Law review"/>
                        <Step1 
                            nextStep={this.nextStep}
                            jumpToEnd={this.jumpToEnd}
                            values={values}
                        />
                    </MuiThemeProvider>
                )

            case 'End':
                return (
                    <MuiThemeProvider>
                        <AppBar title="Stark Law review"/>
                        <Ending 
                            prevStep={this.prevStep}
                            values={values}
                        />
                    </MuiThemeProvider>
                )
        }
    }
}

export default UserForm
