import React, { Component } from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import { Container, Button, Box, Tooltip, 
        FormGroup, FormControl, Checkbox,
        FormHelperText, FormControlLabel, FormLabel } from '@material-ui/core';


export class Step3 extends Component {

    checkboxesGroup = () => {
        const classes = customStyle();
        const [state, setState] = React.useState({
            sPractice: false,
            SProp: false,
            agency: false,
            corp: false,
            partner: false,
            llc: false,
            found: false,
            nonProf: false,
            health: false,
            noneAbove: true, 
        });
    }

    handleCheck = name => event => {
        this.setState({ ...this.state, [name]: event.target.checked});
    }

    
    render() {
        const { sPractice, SProp, agency, corp, 
                partner, llc, found, nonProf,
                health, noneAbove
            } = this.state;

        const boxListNames = ['sPractice', 'SProp', 'agency', 'corp', 
            'partner', 'llc', 'found', 'nonProf',
            'health', 'noneAbove']
        const boxList = [sPractice, SProp, agency, corp, 
        partner, llc, found, nonProf,
        health, noneAbove]

        const error = boxList.filter(v => v).length < 1;

        return (
            <MuiThemeProvider>
                <React.Fragment>
                    <Container maxwidth='sm'>
                        <h1>What type of entity is it?</h1>
                        <Box component='span'>
                            <Container maxWidth='sm'>
                                <FormControl component='fieldset' className={classes.FormControl}>
                                    <FormLabel component='legend'>
                                    Select one
                                    <FormGroup>
                                        {boxListNames
                                            .map(n => 
                                            <FormControlLabel
                                                control={<Checkbox checked={n} 
                                                onChange={this.handleCheck(n)}
                                                value={n}/>}
                                            />)
                                            .reduce((prev, cur) => [prev, ' ', curr])}
                                    </FormGroup>
                                    </FormLabel>
                                </FormControl>
                            </Container>
                        </Box>
                    </Container>
                </React.Fragment>
            </MuiThemeProvider>
        )
    }
}

const customStyle = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(3),
    }
}));

export default Step3
